        address[] calldata _stakingPools
    ) external onlyOwner {
        uint256 _length = _stakingPools.length;
        for (uint256 _i; _i < _length; ) {
            _removeBLPStakingPoolInternal(_stakingPools[_i]);

            unchecked {
                ++_i;
            }
