                value = value + _staked * _price;
            }

            unchecked {
                ++_i;
            }
        }
    }

    function _getSupplyValue(
        address _account
    ) internal returns (uint256 value, bool status) {
        address[] memory _iTokens = validSupplies.values();
        uint256 _len = _iTokens.length;
